theme.css:
:root {
  --n3-primary-color: #2563EB;
  --n3-secondary-color: #5856D6;
  --n3-background-color: #FFFFFF;
  --n3-foreground-color: #111827;
  --n3-font-family: Inter, system-ui, -apple-system, Segoe UI, Roboto, sans-serif;
  --n3-font-size-base: 14px;
  --n3-font-weight: 500;
  --n3-spacing-scale: 1.0;
  --n3-border-radius: 10px;
  --n3-shadow-elevation: 0 2px 6px rgba(15, 23, 42, 0.08);
}

body.n3-runtime,
body {
  background: var(--n3-background-color);
  color: var(--n3-foreground-color);
  font-family: var(--n3-font-family);
  font-size: var(--n3-font-size-base);
  font-weight: var(--n3-font-weight);
}

.ui-body,
.panel,
.ui-element,
.list-item,
.trace-row,
.n3-layout-header,
.n3-layout-sidebar,
.n3-layout-main,
.n3-layout-drawer,
.n3-layout-footer {
  background: var(--n3-background-color);
  color: var(--n3-foreground-color);
  border-radius: var(--n3-border-radius);
  box-shadow: var(--n3-shadow-elevation);
}

.btn.primary,
.ui-chat-action,
.ui-upload-label {
  background: var(--n3-primary-color);
  border-color: var(--n3-primary-color);
  color: #FFFFFF;
}

.ui-link,
.n3-chat-attachment-link,
.n3-citation-chip {
  color: var(--n3-primary-color);
}

.ui-card-title,
.ui-section-title,
.n3-chat-group-label {
  color: var(--n3-secondary-color);
}

.ui-element,
.panel,
.n3-layout-header,
.n3-layout-sidebar,
.n3-layout-main,
.n3-layout-drawer,
.n3-layout-footer {
  padding: calc(10px * var(--n3-spacing-scale));
}

.ui-row,
.ui-column,
.ui-card-group,
.n3-layout-root,
.n3-layout-body {
  gap: calc(10px * var(--n3-spacing-scale));
}
